is_monotonic([]) -> true;
is_monotonic([_]) -> true;
is_monotonic([First, Second | Tail]) ->
    case Second >= First of
        true -> check_non_decreasing([Second | Tail]);
        false -> check_non_increasing([Second | Tail])
    end.

check_non_decreasing([]) -> true;
check_non_decreasing([_]) -> true;
check_non_decreasing([First, Second | Tail]) ->
    First =< Second andalso check_non_decreasing([Second | Tail]).

check_non_increasing([]) -> true;
check_non_increasing([_]) -> true;
check_non_increasing([First, Second | Tail]) ->
    First >= Second andalso check_non_increasing([Second | Tail]).

determine_sign(N) when N > 0 -> positive;
determine_sign(0) -> zero;
determine_sign(N) when N < 0 -> negative.
